# frozen_string_literal: true

RSpec.describe QrcodePixRuby do
  before :each do
    @pix                = QrcodePixRuby::Payload.new
    @pix.pix_key        = 'pedro.felipe.azevedo.furtado@gmail.com'
    @pix.merchant_name  = 'PEDRO FURTADO'
    @pix.merchant_city  = 'SAO PAULO'

    @dynamic_pix                = QrcodePixRuby::Payload.new
    @dynamic_pix.url            = 'https://example.com'
    @dynamic_pix.merchant_name  = 'PEDRO FURTADO'
    @dynamic_pix.merchant_city  = 'SAO PAULO'
  end

  it 'QRCode - Basic' do
    expect(@pix.payload).to eq '00020101021226600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com5204000053039865802BR5913PEDRO FURTADO6009SAO PAULO62070503***63046B4C'
    expect(@pix.base64).to eq 'data:image/png;base64,'
  end

  it 'QRCode - Currency' do
    @pix.currency = '986'
    expect(@pix.payload).to eq '00020101021226600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com5204000053039865802BR5913PEDRO FURTADO6009SAO PAULO62070503***63046B4C'
    expect(@pix.base64).to eq 'data:image/png;base64,'

    @pix.currency = '123'
    expect(@pix.payload).to eq '00020101021226600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com5204000053031235802BR5913PEDRO FURTADO6009SAO PAULO62070503***63047E2C'
    expect(@pix.base64).to eq 'data:image/png;base64,'
  end

  it 'QRCode - Amount' do
    @pix.amount = '100.99'
    expect(@pix.payload).to eq '00020101021226600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com5204000053039865406100.995802BR5913PEDRO FURTADO6009SAO PAULO62070503***63045059'
    expect(@pix.base64).to eq 'data:image/png;base64,'

    @pix.amount = '0.01'
    expect(@pix.payload).to eq '00020101021226600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com52040000530398654040.015802BR5913PEDRO FURTADO6009SAO PAULO62070503***6304A136'
    expect(@pix.base64).to eq 'data:image/png;base64,'
  end

  it 'QRCode - Country code' do
    @pix.country_code = 'BR'
    expect(@pix.payload).to eq '00020101021226600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com5204000053039865802BR5913PEDRO FURTADO6009SAO PAULO62070503***63046B4C'
    expect(@pix.base64).to eq 'data:image/png;base64,'

    @pix.country_code = 'US'
    expect(@pix.payload).to eq '00020101021226600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com5204000053039865802US5913PEDRO FURTADO6009SAO PAULO62070503***63049DF5'
    expect(@pix.base64).to eq 'data:image/png;base64,'
  end

  it 'QRCode - Postal code' do
    @pix.postal_code = '01134000'
    expect(@pix.payload).to eq '00020101021226600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com5204000053039865802BR5913PEDRO FURTADO6009SAO PAULO61080113400062070503***630497E2'
    expect(@pix.base64).to eq 'data:image/png;base64,'

    @pix.postal_code = '01131010'
    expect(@pix.payload).to eq '00020101021226600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com5204000053039865802BR5913PEDRO FURTADO6009SAO PAULO61080113101062070503***63041100'
    expect(@pix.base64).to eq 'data:image/png;base64,'
  end

  it 'QRCode - Transaction ID' do
    @pix.transaction_id = 'PIX123'
    expect(@pix.payload).to eq '00020101021226600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com5204000053039865802BR5913PEDRO FURTADO6009SAO PAULO62100506PIX12363046BE4'
    expect(@pix.base64).to eq 'data:image/png;base64,'

    @pix.transaction_id = 'PIXPEDRO'
    expect(@pix.payload).to eq '00020101021226600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com5204000053039865802BR5913PEDRO FURTADO6009SAO PAULO62120508PIXPEDRO630428E7'
    expect(@pix.base64).to eq 'data:image/png;base64,'
  end

  it 'QRCode - Description' do
    @pix.description = 'Bora pagar?'
    expect(@pix.payload).to eq '00020101021226750014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com0211Bora pagar?5204000053039865802BR5913PEDRO FURTADO6009SAO PAULO62070503***6304BC58'
    expect(@pix.base64).to eq 'data:image/png;base64,'

    @pix.description = 'PIX 123'
    expect(@pix.payload).to eq '00020101021226710014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com0207PIX 1235204000053039865802BR5913PEDRO FURTADO6009SAO PAULO62070503***6304D936'
    expect(@pix.base64).to eq 'data:image/png;base64,'
  end

  it 'QRCode - Repeatable' do
    @pix.repeatable = false
    expect(@pix.payload).to eq '00020101021226600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com5204000053039865802BR5913PEDRO FURTADO6009SAO PAULO62070503***63046B4C'
    expect(@pix.base64).to eq 'data:image/png;base64,'

    @pix.repeatable = true
    expect(@pix.payload).to eq '00020101021126600014BR.GOV.BCB.PIX0138pedro.felipe.azevedo.furtado@gmail.com5204000053039865802BR5913PEDRO FURTADO6009SAO PAULO62070503***630468A4'
    expect(@pix.base64).to eq 'data:image/png;base64,'
  end

  it 'QRCode - URL' do
    #expect(@dynamic_pix.payload).to eq ''
    #expect(@dynamic_pix.base64).to eq ''

    @dynamic_pix.url = 'http://another-example.com'
    #expect(@dynamic_pix.payload).to eq ''
    #expect(@dynamic_pix.base64).to eq ''
  end

  it 'QRCode - Base64' do
    expect(@pix.base64).to eq 'data:image/png;base64,'
  end
end
